    background-position: bottom center;
    padding-top: 66px;
  }
  //layout spacing
  .feature-single__article section > div {
    max-width: 1100px;
    margin: 0 auto;
    padding: 0 40px;
    padding-left: var(
      --paddingLeft,
      30px
    ); // left padding should equal .main-navigation padding-left
    &.hero__wrapper {
      padding: 30px 40px;
      padding-left: var(--paddingLeft, 30px);
    }
    &.content__wrapper {
      max-width: 700px;
    }
  }

  //hero layout
  .hero__wrapper {
    display: flex;
    justify-content: space-between;
    //align-items:center;
    .hero-image {
      max-width: 300px;
    }
  }
  .hero__content {
    h1 {
      color: white;
    }
  }
}

.article__content {
  padding-top: 60px;
}
