//--/--/--/--/--/--/--/--/--/--/--/--/--/--/--/--/--//
//Header bar
//site logo and navigation
//--/--/--/--/--/--/--/--/--/--/--/--/--/--/--/--/--//
.bg-root {
  position: absolute;
  top: 0;
  left: 0;
  transition: transform 0.2s;
  transform: rotateX(-5deg);
  &[data-active] {
    transform: none;
  }
}
.animate-sub-menu-bg {
  //this element gets placed dynamically
  background: white;
  border-radius: $cardBorderRadius;
  box-shadow: $largeShadow;
  visibility: hidden;
  opacity: 0;
  position: absolute;
  pointer-events: none;
  top: 0;
  left: 0;
  width: 20px;
  height: 20px;
  //transform: translateY(30px) rotateX(-15deg);
  transition: transform $timingQuick $easing, opacity 0.1s linear;
  transform-origin: 50% 0%;
  display: flex;
  justify-content: center;
  z-index: 0;
}
.bg-root[data-active] .animate-sub-menu-bg {
  opacity: 1;
  visibility: visible;
}
html body .main-header-bar .main-header-menu .menu-item:hover {
  .sub-menu {
    transition: opacity 0.15s linear;
    opacity: 0;
  }
  &.trigger-enter .sub-menu {
    visibility: visible;
  }
  &.trigger-enter-active .sub-menu {
    opacity: 1;
    transition: opacity 0.12s linear;
  }
}

body .main-header-bar .sub-menu li a {
  color: $primaryBlue;
}
body .main-header-bar {
  perspective: 200px;
  line-height: 1 !important;
  //navigation stuf

  .main-header-menu .sub-menu {
    background: transparent;
    box-shadow: 0 0 0;
    border-top: 0;
  }

  .menu-item a {
    color: white;
    &:hover {
      text-decoration: none;
      transition: all $timingQuick $easing;
      transform: translateY(-1px);
      opacity: 0.8;
    }
  }

  //logo stuff
  .ast-site-title-wrap {
    h1, .site-title {
      display:block;
      height: 50px;
      width: 200px;
      position: relative;
      background-repeat: no-repeat;
      background-image: url(/images/clearviewlogo.svg);
      background-size: contain;
      margin-top: 0;
      a {
        position: absolute;
        top: 0;
        left: 0;
        width: 0;
        height: 0;
        opacity: 0;
      }
    }
  }
}
//mobile icon
.ast-header-break-point .menu-toggle .menu-toggle-icon:before {
  color: white !important;
}

