//--/--/--/--/--/--/--/--/--/--/--//
// this class is added to a column that has an image and UABB heading
//--/--/--/--/--/--/--/--/--/--/--//
.two-column-flex .supporting-card {
  position: relative;
  //this is the container for the image and testimony
  & > div {
    display: grid;
    grid-template-columns: 50px 50px 1fr;
    align-items: center;
    @media (max-width: $mediumScreen) {
      grid-template-columns: 100%;
      grid-template-rows: 30px 50px 1fr;
      .fl-photo-content {
        max-width: 100px;
        min-width: auto;
        grid-row: 1/3;
      }
      .fl-module-uabb-heading {
        position: relative;
        grid-column: 1/-1;
        grid-row: 2/4;
        padding-left: 20px;
        padding-top: 40px;
      }
      .fl-photo-content {
        img {
          display: block;
          min-width: 100%;
        }
      }
    }
  }
  &.no-graphic {
    width: auto !important;
    margin-left: 0;
    > div {
      display: block;
    }
    .fl-module-uabb-heading {
      padding: 0px 20px 0 10px;
      border-left: 7px solid var(--color);
    }
    h5 span {
      color: var(--color);
      font-weight: bold;
      text-transform: uppercase;
    }
  }
  //photo wrapper
  .fl-photo-content {
    z-index: 3;
    min-width: 100px;
    border-radius: 50%;
    overflow: hidden;
    border-color: var(--border-color, $primaryBlue);
    border-width: 6px;
    border-style: solid;
  }
  img {
    transform: scale(1.01); //eliminates any white on the image
  }
  //heading & paragraph
  .fl-module-uabb-heading {
    z-index: 1;
    h5 {
      font-size: 16px;
      color: $mediumGrey;
      font-weight: 400;
      margin-bottom: 10px;
    }
    p {
      font-size: 16px;
    }
    box-shadow: $smallShadow;
    background: #f9f9f9;
    border-radius: $cardBorderRadius;
    padding-left: 70px;
    grid-column: 2/-1;
  }
}

//change the border-color of the image
.supporting-card {
  &.border-yellow {
    --border-color: var(--yellow);
  }
  &.border-red {
    --border-color: var(--red);
  }
  &.border-green {
    --border-color: var(--primaryGreen);
  }
  &.border-purple {
    --border-color: var(--purple);
  }
}

